const template = html<FrenchPage>` <iframe></iframe> `;

const styles = css`
  :host {
    display: block;
    width: 100%;
    height: 100%;
  }

  :host(:not([active])) {
    display: none;
  }

  iframe {
    width: 100%;
    height: 100%;
    border: none;
  }
`;

@customElement({ name: 'french-page', template, styles })
export class FrenchPage extends FASTElement {
  @attr({ mode: 'boolean' }) active = false;
  @inject(EdgeSettingsSerivce) ss!: EdgeSettingsSerivce;
  _iframeElement?: HTMLIFrameElement;

  connectedCallback(): void {
    super.connectedCallback();
    this.setElements();
    this.addEventListeners();
    this.handleChange(null, 'translatePage');
  }

  disconnectedCallback(): void {
    super.disconnectedCallback();
    this.unsetElements();
    this.removeEventListeners();
  }

  setElements() {
    this._iframeElement = this.shadowRoot?.querySelector(
      'iframe',
    ) as HTMLIFrameElement;
  }

  unsetElements() {
    this._iframeElement = undefined;
  }

  addEventListeners() {
    if (this._iframeElement) {
      this._iframeElement.onload = () => this.$emit('pageload');
    }
    Observable.getNotifier(this.ss).subscribe(this, 'translatePage');
  }

  removeEventListeners() {
    if (this._iframeElement) {
      this._iframeElement.onload = null;
    }
    Observable.getNotifier(this.ss).unsubscribe(this, 'translatePage');
  }

  handleChange(subject: unknown, prop: string) {
    if (prop === 'translatePage') {
      if (this._iframeElement) {
        if (this.ss.translatePage) {
          this._iframeElement.srcdoc = translatedPage;
        } else {
          this._iframeElement.srcdoc = page;
        }
      }
    }
  }
}
